mapper.select:
select 

from flow
where id = #{id,jdbcType=INTEGER}
 
   and  create_user_id = #{userId,jdbcType=VARCHAR}

select
from flow where team_id= #{teamId,jdbcType=INTEGER} and state=0
and create_user_id = #{userId,jdbcType=VARCHAR}

select
from flow where name= #{name,jdbcType=VARCHAR} and state=0
and team_id= #{teamId,jdbcType=INTEGER}
